sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue == '') {
      return;
   }

	var whatneeded = g_form.getValue('u_what_needed');

	// Clear all options in What neeeded select box
	g_form.clearOptions('u_what_needed');

	// Set option values dinamically based on selected Request Type: Case of hr
	if (newValue == 'hr'){
		g_form.addOption('u_what_needed', 'hr1', 'Human Resources 1');
		g_form.addOption('u_what_needed', 'hr2', 'Human Resources 2');
		g_form.addOption('u_what_needed', 'other', 'Other');
	}

	// Set option values dinamically based on selected Request Type: Case of facilities
	if (newValue == 'facilities'){
		g_form.addOption('u_what_needed', 'facilities1', 'Facilities 1');
		g_form.addOption('u_what_needed', 'facilities2', 'Facilities 2');
		g_form.addOption('u_what_needed', 'other', 'Other');
		}

	// Set option values dinamically based on selected Request Type: Case of legal
	if (newValue == 'legal'){
		g_form.addOption('u_what_needed', 'legal1', 'Soft unko');
		g_form.addOption('u_what_needed', 'legal2', 'Hard unko');
		g_form.addOption('u_what_needed', 'other', 'Other unko');
		}

	// The dinamical control in above is not applicable while the record is on loading, 
	// or if its not newly created record 
	if (isLoading && !g_form.isNewRecord()){
		g_form.setValue('u_what_needed', whatneeded);
	}   
}
